import React, { useState } from 'react';
const Cherry =()=>{
    const[num,setNum]= useState(0);
    const creepy =()=>{

        setNum(num +1);
    };
    const jimmy =()=>{
        
           if(num>0){ 
            setNum(num-1);
           }else {
               alert("sorry")
               setNum(0);
           };
      
    };
    return(
        <>
            <h1>{num}</h1>
            <button onClick={creepy}>Increase</button>
            <button onClick={jimmy}>decrease</button>
        </>
    );

}
export default Cherry;
